import React from 'react';
import './Cards.css';
import { FormControl, useFormControlContext } from '@mui/base/FormControl';
import { Input, inputClasses } from '@mui/base/Input';
import { styled } from '@mui/system';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { Button, buttonClasses } from '@mui/base/Button';




const ButtonRoot = React.forwardRef(function ButtonRoot(props, ref) {
    const { children, ...other } = props;

    return (
      <svg width="150" height="50" {...other} ref={ref}>
        <polygon points="0,50 0,0 150,0 150,50" className="bg" />
        <polygon points="0,50 0,0 150,0 150,50" className="borderEffect" />
        <foreignObject x="0" y="0" width="150" height="50">
          <div className="content">{children}</div>
        </foreignObject>
      </svg>
    );
  });

  ButtonRoot.propTypes = {
    children: PropTypes.node,
  };

  const SvgButton = React.forwardRef(function SvgButton(props, ref) {
    return <Button {...props} slots={{ root: CustomButtonRoot }} ref={ref} />;
  });



function InfoCard() {
  return (
    <div className='cardsInfo'>
        <div className='container-info-row'>
           <div className='info'>
                  <h1> Brindanos tu informacion y <br/>en breves uno de nuestros agentes <br/>te estará contactando</h1>
                  <FormControl style={{marginBottom: '40px'}} defaultValue="" required>
                <Label>Nombre Completo</Label>
                <StyledInput placeholder="Ingresa tu nombre aquí..." />
                <HelperText />
                </FormControl>
                <FormControl style={{marginBottom: '40px'}}  defaultValue="" required>
                <Label>Correo Electronico </Label>
                <StyledInput placeholder="Ingresa tu correo electrónico..." />
                <HelperText />
                </FormControl>
                <FormControl style={{marginBottom: '40px'}}  defaultValue="" required>
                <Label>Número de Teléfono</Label>
                <StyledInput placeholder="Ingresa tu número telefónico..." />
                <HelperText />
                </FormControl>
                <SvgButton >Enviar</SvgButton>

           </div>
           <div className='logo-column'>

                <div className='logo-column'>
                <img className='imagen' src='./images/HaciendaPlayaBlanca-Blanco.png' style={{ width: '200px', height: 'auto'}} />
                <img className='imagen' src='./images/TerraticaLogo.png'  style={{ width: '200px', height: 'auto'}} />
                </div>

           </div>
        </div>
    </div>
  );
}

export default InfoCard;


const StyledInput = styled(Input)(
    ({ theme }) => `

    .${inputClasses.input} {
      width: 320px;
      font-family: 'IBM Plex Sans', sans-serif;
      font-size: 0.875rem;
      font-weight: 400;
      line-height: 1.5;
      padding: 8px 12px;
      border-radius: 8px;
      color: ${theme.palette.mode === 'dark' ? grey[300] : grey[900]};
      background: ${theme.palette.mode === 'dark' ? grey[900] : '#fff'};
      border: 1px solid ${theme.palette.mode === 'dark' ? grey[700] : grey[200]};
      box-shadow: 0px 2px 2px ${theme.palette.mode === 'dark' ? grey[900] : grey[50]};

      &:hover {
        border-color: ${blue[400]};
      }

      &:focus {
        outline: 0;
        border-color: ${blue[400]};
        box-shadow: 0 0 0 3px ${theme.palette.mode === 'dark' ? blue[600] : blue[200]};
      }
    }
  `,
  );

  const Label = styled(({ children, className }) => {
    const formControlContext = useFormControlContext();
    const [dirty, setDirty] = React.useState(false);

    React.useEffect(() => {
      if (formControlContext?.filled) {
        setDirty(true);
      }
    }, [formControlContext]);

    if (formControlContext === undefined) {
      return <p>{children}</p>;
    }

    const { error, required, filled } = formControlContext;
    const showRequiredError = dirty && required && !filled;

    return (
      <p className={clsx(className, error || showRequiredError ? 'invalid' : '')}>
        {children}
        {required ? ' *' : ''}
      </p>
    );
  })`
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
    margin-bottom: 4px;

    &.invalid {
      color: red;
    }
  `;

  const HelperText = styled((props) => {
    const formControlContext = useFormControlContext();
    const [dirty, setDirty] = React.useState(false);

    React.useEffect(() => {
      if (formControlContext?.filled) {
        setDirty(true);
      }
    }, [formControlContext]);

    if (formControlContext === undefined) {
      return null;
    }

    const { required, filled } = formControlContext;
    const showRequiredError = dirty && required && !filled;

    return showRequiredError ? <p {...props}>Campo requerido</p> : null;
  })`
    font-family: 'IBM Plex Sans', sans-serif;
    font-size: 0.875rem;
  `;



  const grey = {
    50: '#F3F6F9',
    100: '#E5EAF2',
    200: '#DAE2ED',
    300: '#C7D0DD',
    400: '#B0B8C4',
    500: '#9DA8B7',
    600: '#6B7A90',
    700: '#434D5B',
    800: '#303740',
    900: '#1C2025',
  };




const blue = {
    50: '#F0F7FF',
    100: '#C2E0FF',
    200: '#99CCF3',
    400: '#3399FF',
    500: '#007FFF',
    600: '#0072E6',
    700: '#0059B3',
    800: '#004C99',
    900: '#003A75',
  };

  const CustomButtonRoot = styled(ButtonRoot)(
    ({ theme }) => `
    overflow: visible;
    cursor: pointer;
    --main-color: ${theme.palette.mode === 'light' ? blue[600] : blue[200]};
    --hover-color: ${theme.palette.mode === 'light' ? blue[50] : blue[900]};
    --active-color: ${theme.palette.mode === 'light' ? blue[100] : blue[800]};

    & polygon {
      fill: transparent;
      transition: all 800ms ease;
      pointer-events: none;
    }

    & .bg {
      stroke: var(--main-color);
      stroke-width: 1;
      filter: drop-shadow(0 4px 16px rgba(0, 0, 0, 0.1));
      fill: transparent;
    }

    & .borderEffect {
      stroke: var(--main-color);
      stroke-width: 2;
      stroke-dasharray: 120 600;
      stroke-dashoffset: 120;
      fill: transparent;
    }

    &:hover,
    &.${buttonClasses.focusVisible} {
      .borderEffect {
        stroke-dashoffset: -600;
      }

      .bg {
        fill: var(--hover-color);
      }
    }

    &:focus,
    &.${buttonClasses.focusVisible} {
      outline: 2px solid ${theme.palette.mode === 'dark' ? blue[700] : blue[200]};
      outline-offset: 2px;
    }

    &.${buttonClasses.active} {
      & .bg {
        fill: var(--active-color);
        transition: fill 150ms ease-out;
      }
    }

    & foreignObject {
      pointer-events: none;

      & .content {
        font-size: 0.875rem;
        font-family: 'IBM Plex Sans', sans-serif;
        font-weight: 600;
        line-height: 1.5;
        height: 100%;
        display: flex;
        align-items: center;
        justify-content: center;
        color: var(--main-color);
      }

      & svg {
        margin: 0 4px;
      }
    }`,
  );
