import React, { useState, useEffect } from 'react';
import { Button } from './Button';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [prevScrollPos, setPrevScrollPos] = useState(window.pageYOffset);

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  const handleScroll = () => {
    const currentScrollPos = window.pageYOffset;
    setClick(false);

    if (prevScrollPos > currentScrollPos) {
      setButton(true); // Muestra el navbar al hacer scroll hacia arriba
    } else {
      setButton(false); // Oculta el navbar al hacer scroll hacia abajo
    }

    setPrevScrollPos(currentScrollPos);
  };

  useEffect(() => {
    showButton();
    window.addEventListener('resize', showButton);
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('resize', showButton);
      window.removeEventListener('scroll', handleScroll);
    };
  }, [prevScrollPos]);

  return (
    <>
      <nav className={button ? 'navbar' : 'navbar hidden'}>
        <div className='navbar-container'>
          <Link to='/' className='navbar-logo' onClick={closeMobileMenu}>
            <img src='./images/HaciendaPlayaBlanca-Negro.png' style={{ width: '100%', height: 'auto',  maxWidth:'15px' }}/>
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
          </div>
          <ul className={click ? 'nav-menu active' : 'nav-menu'}>
            <li className='nav-item'>
              <Link to='/' className='nav-links' onClick={closeMobileMenu}>
                Inicio
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/listings'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Planos
              </Link>
            </li>
            <li className='nav-item'>
              <Link
                to='/contact-us'
                className='nav-links'
                onClick={closeMobileMenu}
              >
                Contáctenos
              </Link>
            </li>
            {/* Agrega más elementos según sea necesario */}
          </ul>
        </div>
      </nav>
    </>
  );
}

export default Navbar;
